import "semantic-ui-css/semantic.min.css";
import React, { useState } from "react";
import { Grid, Header, Segment, Table, Tab } from "semantic-ui-react";
import ApiCall from "../api/ApiCall";

const style = {
  h1: {
    marginTop: "2em",
    fontSize: "35px",
  },
  container: {
    marginLeft: 50,
    marginRight: 50,
  },
};

let panes = [];

function RATP() {
  const [listeStations, setListeStations] = useState({});

  return (
    <div>
      <ApiCall setData={setListeStations}>
        https://api-ratp.pierre-grimaud.fr/v4/stations/metros/9
      </ApiCall>
      {displayStations(listeStations, panes)}
      <Header as="h1" content="API RATP" textAlign="center" style={style.h1} />
      <Grid columns={2} stackable style={style.container}>
        <Grid.Row columns={1}>
          <Grid.Column>
            <Segment>
              <Tab panes={panes} />
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </div>
  );
}

export default RATP;

function displayStations(stations, pan) {
  if (typeof stations.result != "undefined") {
    stations.result.stations.map((data) =>
      panes.push({
        menuItem: { data },
        render: () => <Tab.Pane>Tab 1 Content</Tab.Pane>,
      })
    );
  }
}
