import "semantic-ui-css/semantic.min.css";
import React, { useState } from "react";
import { Grid, Header, Segment, List } from "semantic-ui-react";

import ApiCall from "../api/ApiCall.js";

const style = {
  h1: {
    fontSize: "35px",
  },
  h2: {
    fontSize: "24px",
  },
  container: {
    marginLeft: 50,
    marginRight: 50,
  },
};

function Exemple() {
  const [airQuality, setAirQuality] = useState({});
  return (
    <div>
      <ApiCall setData={setAirQuality}>
        http://api.airvisual.com/v2/nearest_city?key=7b8930b6-8d0c-4fb6-a63b-da7c8e2ed6c7
      </ApiCall>
      {renderPage(airQuality)}
    </div>
  );
}

export default Exemple;

function renderPage(data) {
  if (typeof data.status != "undefined") {
    return (
      <>
        <Header
          as="h1"
          content="API Exemple"
          textAlign="center"
          style={style.h1}
        />
        <Grid columns={1} stackable style={style.container}>
          <Grid.Row columns={2}>
            <Grid.Column>
              <Segment></Segment>
            </Grid.Column>
            <Grid.Column></Grid.Column>
          </Grid.Row>
        </Grid>
      </>
    );
  }
}

function getQuality(aqi) {
  if (aqi <= 50) {
    return "Bon";
  } else if (aqi > 50 && aqi <= 100) {
    return "Modéré";
  } else if (aqi > 100 && aqi <= 150) {
    return "Mauvais pour les populations sensibles";
  } else if (aqi > 150 && aqi <= 200) {
    return "Mauvais";
  } else if (aqi > 200 && aqi <= 300) {
    return "Très mauvais";
  } else {
    return "Dangeureux";
  }
}

function getPollutionType(type) {
  switch (type) {
    case "p2":
      return "pm2.5";
    case "p1":
      return "pm10";
    case "o3":
      return "Ozone";
    case "n2":
      return "Dioxyde d'hydrogène";
    case "s2":
      return "Dioxyde de sulfure";
    case "co":
      return "Monoxyde de carbone";
    default:
      return "-Aucune donnée-";
  }
}
