import "semantic-ui-css/semantic.min.css";
import React, { useState } from "react";
import { Grid, Header, Segment, Table } from "semantic-ui-react";
import ApiCall from "../api/ApiCall";

const style = {
  h1: {
    marginTop: "2em",
    fontSize: "35px",
  },
};

function RATP() {
  const [listeStations, setListeStations] = useState({});
  return (
    <div>
      <ApiCall setData={setListeStations}>
        https://api-ratp.pierre-grimaud.fr/v4/stations/metros/9
      </ApiCall>
      <Header as="h1" content="API RATP" textAlign="center" style={style.h1} />
      <Grid columns={2} stackable>
        <Grid.Row columns={3}>
          <Grid.Column></Grid.Column>
          <Grid.Column>
            <Segment>
              <Table celled>
                <Table.Header>
                  <Table.Row>
                    <Table.HeaderCell>Stations</Table.HeaderCell>
                  </Table.Row>
                </Table.Header>
                {console.log(listeStations)}
                if(listeStations !== null)
                {listeStations.map((data) => (
                  <Table.Body>
                    <Table.Row>
                      <Table.Cell key={data.slug}>{data.name}</Table.Cell>
                    </Table.Row>
                  </Table.Body>
                ))}
              </Table>
            </Segment>
          </Grid.Column>
          <Grid.Column></Grid.Column>
        </Grid.Row>
      </Grid>
    </div>
  );
}

export default RATP;
