import "semantic-ui-css/semantic.min.css";
import React, { useState } from "react";
import { Grid, Header, Segment, List } from "semantic-ui-react";

import ApiCall from "../api/ApiCall.js";

const style = {
  h1: {
    marginTop: "2em",
    fontSize: "35px",
  },
  container: {
    marginLeft: 50,
    marginRight: 50,
  },
};

function IqAir() {
  const [airQuality, setAirQuality] = useState({});
  return (
    <div>
      <ApiCall setData={setAirQuality}>
        http://api.airvisual.com/v2/nearest_city?key=7b8930b6-8d0c-4fb6-a63b-da7c8e2ed6c7
      </ApiCall>
      {renderPage(airQuality)}
    </div>
  );
}

export default IqAir;

function renderPage(data) {
  if (typeof data.status != "undefined") {
    return (
      <>
        <Header
          as="h1"
          content="API IqAir"
          textAlign="center"
          style={style.h1}
        />
        <Grid columns={1} stackable style={style.container}>
          <Grid.Row columns={2}>
            <Grid.Column>
              <Segment>
                <List divided relaxed>
                  <List.Item>
                    <List.Icon
                      name="github"
                      size="large"
                      verticalAlign="middle"
                    />
                    <List.Content>
                      <List.Header as="p">Ville : {data.data.city}</List.Header>
                    </List.Content>
                  </List.Item>
                  <List.Item>
                    <List.Icon
                      name="github"
                      size="large"
                      verticalAlign="middle"
                    />
                    <List.Content>
                      <List.Header as="p">
                        DÃ©partement : {data.data.state}
                      </List.Header>
                    </List.Content>
                  </List.Item>
                  <List.Item>
                    <List.Icon
                      name="github"
                      size="large"
                      verticalAlign="middle"
                    />
                    <List.Content>
                      <List.Header as="p">
                        Semantic-Org/Semantic-UI-Meteor
                      </List.Header>
                    </List.Content>
                  </List.Item>
                </List>
              </Segment>
            </Grid.Column>
            <Grid.Column>
              <Segment></Segment>
            </Grid.Column>
            <Grid.Column></Grid.Column>
          </Grid.Row>
        </Grid>
      </>
    );
  }
}
