import "semantic-ui-css/semantic.min.css";
import React, { useState } from "react";
import {
  Button,
  Container,
  Grid,
  Header,
  Icon,
  Image,
  Item,
  Label,
  Menu,
  Segment,
  Step,
  Table,
} from "semantic-ui-react";
import ListeStations from "./api/ListeStations";

const style = {
  h1: {
    marginTop: "2em",
    fontSize: "35px",
  },
};

function RATP() {
  const [listeStations, setListeStations] = useState({});
  return (
    <div>
      <ListeStations
        url="https://api-ratp.pierre-grimaud.fr/v4/stations/metros/9"
        setStationsData={setListeStations}
      />
      <Header as="h1" content="API RATP" textAlign="center" style={style.h1} />
      <Grid columns={2} stackable>
        <Grid.Row columns={3}>
          <Grid.Column></Grid.Column>
          <Grid.Column>
            <Segment>
              <Table celled>
                <Table.Header>
                  <Table.Row>
                    <Table.HeaderCell>Stations</Table.HeaderCell>
                  </Table.Row>
                </Table.Header>
                {console.log(listeStations)}
                {listeStations.map((data) => (
                  <Table.Body>
                    <Table.Row>
                      <Table.Cell>{data.name}</Table.Cell>
                    </Table.Row>
                  </Table.Body>
                ))}
              </Table>
            </Segment>
          </Grid.Column>
          <Grid.Column></Grid.Column>
        </Grid.Row>
      </Grid>
    </div>
  );
}

export default RATP;
