import "semantic-ui-css/semantic.min.css";
import React, { useState } from "react";
import { Grid, Header, Segment, Table, Tab } from "semantic-ui-react";
import ApiCall from "../api/ApiCall";

const style = {
  h1: {
    marginTop: "2em",
    fontSize: "35px",
  },
  container: {
    marginLeft: 50,
    marginRight: 50,
  },
};

const panes = [
  { menuItem: "Tab 1", render: () => <Tab.Pane>Tab 1 Content</Tab.Pane> },
  { menuItem: "Tab 2", render: () => <Tab.Pane>Tab 2 Content</Tab.Pane> },
  { menuItem: "Tab 3", render: () => <Tab.Pane>Tab 3 Content</Tab.Pane> },
];

function RATP() {
  const [listeStations, setListeStations] = useState({});

  return (
    <div>
      <ApiCall setData={setListeStations}>
        https://api-ratp.pierre-grimaud.fr/v4/stations/metros/9
      </ApiCall>
      <Header as="h1" content="API RATP" textAlign="center" style={style.h1} />
      <Grid columns={2} stackable style={style.container}>
        <Grid.Row columns={1}>
          <Grid.Column>
            <Segment>
              <Tab panes={panes} />
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </div>
  );
}

export default RATP;

function displayStations(stations) {
  if (typeof stations.result != "undefined") {
    return stations.result.stations.map((data) => (
      <Table.Row key={data.slug}>
        <Table.Cell key={data.slug}>{data.name}</Table.Cell>
      </Table.Row>
    ));
  }
}
