import "semantic-ui-css/semantic.min.css";
import { Header, Segment } from "semantic-ui-react";

const style = {
  h1: {
    marginTop: "2em",
    fontSize: "35px",
  },
  segment: {
    marginLeft: 50,
    marginRight: 50,
  },
};

function Accueil() {
  return (
    <div>
      <Header
        as="h1"
        content="TP React API"
        textAlign="center"
        style={style.h1}
      />
      <Segment raised style={style.segment}>
        Ce TP de React permet de lier deux APIs sur une interface graphique et
        de les mettre en relation. J'ai choisi ici de créer une application
        liant une API de transport en commun et une API de qualité de l'air afin
        de favoriser un trajet en transport en commun plutôt qu'en voiture en
        fonction de la qualité de l'air
      </Segment>
    </div>
  );
}

export default Accueil;
