import "semantic-ui-css/semantic.min.css";
import React, { useState } from "react";
import {
  Grid,
  Header,
  Segment,
  Table,
  Accordion,
  Icon,
} from "semantic-ui-react";
import ApiCall from "../api/ApiCall";

const style = {
  h1: {
    marginTop: "2em",
    fontSize: "35px",
  },
};

function RATP() {
  const [listeStations, setListeStations] = useState({});

  state = { activeIndex: 0 };

  return (
    <div>
      <ApiCall setData={setListeStations}>
        https://api-ratp.pierre-grimaud.fr/v4/stations/metros/9
      </ApiCall>
      <Header as="h1" content="API RATP" textAlign="center" style={style.h1} />
      <Grid columns={2} stackable>
        <Grid.Row columns={3}>
          <Grid.Column></Grid.Column>
          <Grid.Column>
            <Segment>
              <Accordion>
                <Accordion.Title
                  active={activeIndex === 0}
                  index={0}
                  onClick={this.handleClick}
                >
                  <Icon name="dropdown" />
                  What is a dog?
                </Accordion.Title>
                <Accordion.Content active={activeIndex === 0}>
                  <p>
                    A dog is a type of domesticated animal. Known for its
                    loyalty and faithfulness, it can be found as a welcome guest
                    in many households across the world.
                  </p>
                </Accordion.Content>

                <Accordion.Title
                  active={activeIndex === 1}
                  index={1}
                  onClick={this.handleClick}
                >
                  <Icon name="dropdown" />
                  What kinds of dogs are there?
                </Accordion.Title>
                <Accordion.Content active={activeIndex === 1}>
                  <p>
                    There are many breeds of dogs. Each breed varies in size and
                    temperament. Owners often select a breed of dog that they
                    find to be compatible with their own lifestyle and desires
                    from a companion.
                  </p>
                </Accordion.Content>

                <Accordion.Title
                  active={activeIndex === 2}
                  index={2}
                  onClick={this.handleClick}
                >
                  <Icon name="dropdown" />
                  How do you acquire a dog?
                </Accordion.Title>
                <Accordion.Content active={activeIndex === 2}>
                  <p>
                    Three common ways for a prospective owner to acquire a dog
                    is from pet shops, private owners, or shelters.
                  </p>
                  <p>
                    A pet shop may be the most convenient way to buy a dog.
                    Buying a dog from a private owner allows you to assess the
                    pedigree and upbringing of your dog before choosing to take
                    it home. Lastly, finding your dog from a shelter, helps give
                    a good home to a dog who may not find one so readily.
                  </p>
                </Accordion.Content>
              </Accordion>
            </Segment>
          </Grid.Column>
          <Grid.Column></Grid.Column>
        </Grid.Row>
      </Grid>
    </div>
  );
}

export default RATP;

function displayStations(stations) {
  if (typeof stations.result != "undefined") {
    return stations.result.stations.map((data) => (
      <Table.Row key={data.slug}>
        <Table.Cell key={data.slug}>{data.name}</Table.Cell>
      </Table.Row>
    ));
  }
}

handleClick = (e, titleProps) => {
  const { index } = titleProps;
  const { activeIndex } = this.state;
  const newIndex = activeIndex === index ? -1 : index;

  this.setState({ activeIndex: newIndex });
};
